try:  # import the ladybug_{{cad}} dependencies
    from ladybug_{{cad}}.planarize import curved_solid_faces
    from ladybug_{{cad}}.fromgeometry import from_face3ds_to_joined_brep, from_face3d
    from ladybug_{{cad}}.{{plugin}} import all_required_inputs, give_warning
except ImportError as e:
    raise ImportError('\nFailed to import ladybug_{{cad}}:\n\t{}'.format(e))


if all_required_inputs(ghenv.Component):
    # planarize each of the breps
    pl_brep, smaller_than_tol = [], set()
    for i, brep in enumerate(_brep):
        lb_faces = curved_solid_faces(brep, _mesh_par_, ignore_sliver=False)
        all_lb_faces = []
        for face in lb_faces:
            if face is not None:
                all_lb_faces.append(face)
            else:
                smaller_than_tol.add(i)
        j_brep = from_face3ds_to_joined_brep(all_lb_faces)
        if j_brep is not None:
            pl_brep.extend(j_brep)
        else:
            pl_brep.extend([from_face3d(f) for f in all_lb_faces])

    # if one of the breps has slivers smaller than the tolerance, give a warning
    if smaller_than_tol:
        base_rec = 'Consider chaging meshing parameters. Lowering the {{Cad}} ' \
            'model tolerance and restarting {{Cad}} may also help.'
        for brep_i in smaller_than_tol:
            msg = 'Brep at index {} could not be perfectly planarized at ' \
                'the current {{Cad}} model tolernace and may have gaps or ' \
                'holes.\n{}.'.format(brep_i, base_rec)
            print(msg)
            give_warning(ghenv.Component, msg)
